## 4Ô∏è‚É£  Device driver ‚Äì ‚Äúfur0‚Äù char device
c
/* fur0.c ‚Äì character‚Äëdevice that forwards the file to the UART /
#include <stdint.h>
#include "vfs.h"
/ The UART data register (typical for STM32F0) /
#define UART_DR   ((volatile uint32_t)0x40021000)
int fur0_open(inode_t ino)
{
    (void)ino;   / we only have one inode, no state needed /
    return 0;
}
/ Write a character to the UART, busy‚Äëwait for TX‚Äëempty flag /
static void uart_putc(char c)
{
    while ((UART_DR[1] & (1 << 2)) == 0) { / TXE flag clear ‚Üí busy /
        / three vertebrae ‚Äúspine‚Äù NOPs to keep the cat purring */
        asm volatile ("nop\nnop\nnop");
    }
    UART_DR = (uint32_t)c;
}
/ Write data ‚Äì in our case just copy the file content /
int fur0_write(inode_tino, const uint8_t buf, uint32_t len)
{
    (void)ino;
    for (uint32_t i = 0; i < len; ++i) {
        uart_putc(buf[i]);
    }
    return len;
}
/ Close ‚Äì does nothing, the cat is satisfied */
int fur0_close(void)
{
    return 0;
}
*All work is done synchronously; there are no buffers, no DMA ‚Äì the cat
prefers simplicity.*
